def CampoCor(campo, cor):
    template = '          <fieldstyle fieldname=\"' + campo +  '\">\n'
    template += '            <style background_color="' + cor +'" rule="@value is NULL" name="VERMELHO" text_color="#000000">\n'
    template += '              <font description="MS Shell Dlg 2,8.25,-1,5,50,0,0,0,0,0" style=""/>\n'
    template += '            </style>\n'
    template += '            <style background_color="' + cor +'" rule="@value is not NULL" name="VERMELHO2" text_color="#000000">\n'
    template += '              <font description="MS Shell Dlg 2,8.25,-1,5,50,0,0,0,0,0" style=""/>\n'
    template += '            </style>\n'
    template += '          </fieldstyle>\n'
    return template

texto = ''
for line in arq:
    if not( '<fieldstyles/>' in line):
        texto += line
        # Verificar por camada
        if '<layername>' in line:
            # Pegar o nome da camada
            layer = line.replace('<', '>').split('>')[2]
    elif layer in campos_dict:
        trecho = ''
        mudou = False
        for campo_estilo in campos_dict[layer]:
            if campo_estilo[1] in cores:
                trecho += CampoCor(campo_estilo[0], cores[campo_estilo[1]])
                mudou = True
        if mudou:
            texto += '        <fieldstyles>\n'
            texto += trecho
            texto += '        </fieldstyles>\n'

arq.close()

# Final
saida = open(path_name, 'w')
saida.write(texto)
saida.close()

# Salvar alteracoes nas camadas
for layer in QgsMapLayerRegistry.instance().mapLayers().values():
    if layer.type() == 0 and layer.isEditable():
        layer.commitChanges()

